import { useNavigate } from "react-router-dom";
import styled from "./Card.module.css"
import { add_fav, remove_fav } from "../../Redux/actions.js"
import { connect } from "react-redux"
import { useState } from "react";


function Card(props) {
   const navigate = useNavigate()

   const [isFav, setIsFav] = useState(false)
   const handleFavorite = () => {
      isFav ? props.remove_fav(props.id)
         : props.add_fav({
            id: props.id,
            name: props.name,
            status: props.status,
            species: props.species
         })
      setIsFav(!isFav)
   }


   return (
      <div className={styled.tarjeta}>
         <button onClick={() => props.onClose(props.id)} className={styled.button}>X</button>
         <h2 className={styled.name}>{props.name}</h2>
         {
            isFav ? (
               <button onClick={handleFavorite}>‚ù§Ô∏è</button>
            ) : (
               <button onClick={handleFavorite}>ü§ç</button>
            )
         }
         <img src={props.image} alt='' onClick={() => navigate(`/detail/${props.id}`)} />
         <h2>Status: {props.status}</h2>
         <h2>Specie: {props.species}</h2>
         <h2>Gender: {props.gender}</h2>
         <h2>Origin: {props.origin}</h2>
      </div>
   );
}


const mapDispatchToProps = (dispatch) => {
   return {
      a√±adirFav: (character) => {
         dispatch(add_fav(character))
      },

      sacarFav: (id) => {
         dispatch(remove_fav(id))
      },
   }
}

export default connect(null, mapDispatchToProps)(Card)
